  public void buildQuery(String orgname, String contact, String address, String reqstat, String offline, String reqstd, String reqend, String reqid, String username) {

	String wc = " 1=1 ";

	if (!orgname.equals("")){
	  wc = wc + " AND UPPER(PARTY_NAME) LIKE UPPER('" + orgname + "') ";
	}
	if (!contact.equals("")){
	  wc = wc + " AND UPPER(CONTACT) LIKE UPPER('" + contact + "') ";
	}
	if (!address.equals("")){
	  wc = wc + " AND UPPER(BILL_TO_ADDRESS) LIKE UPPER('" + address + "') ";
	}
	if (!reqstat.equals("")){
	  wc = wc + " AND STATUS LIKE '" + reqstat + "' ";
	}
	if (!offline.equals("")){
	  wc = wc + " AND OFFLINE_CREATED_FLAG LIKE '" + offline + "' ";
	}
	if (!reqstd.equals("") && !reqend.equals("")){
	  wc = wc + " AND trunc(CREATION_DATE) BETWEEN '" + reqstd + "' AND '"+ reqend + "' ";
	}
	if (!reqid.equals("")){
	  wc = wc + " AND REQUEST_ID LIKE '" + reqid + "' ";
	}


    ODASLSrchVOImpl vo = getODASLSrchVO();
//    vo.clearCache();

    vo.initBigQuery(wc, username);

  }

